# Every step, the sea cucumbers in the east-facing herd attempt to move forward one location,
# then the sea cucumbers in the south-facing herd attempt to move forward one location

data = [list(line) for line in input.split('\n')]
h, w = len(data), len(data[0])

def find_east(data):
    res = []
    for y in range(h):
        for x in range(w):
            xn = (x + 1) if (x + 1) < w else 0
            if data[y][x] == '>' and data[y][xn] == '.':
                res.append((x, y))
    return res

def move_east(data, coords):
    for x, y in coords:
        xn = (x + 1) if (x + 1) < w else 0
        data[y][x], data[y][xn] = data[y][xn], data[y][x]

def find_south(data):
    res = []
    for y in range(h):
        for x in range(w):
            yn = (y + 1) if (y + 1) < h else 0
            if data[y][x] == 'v' and data[yn][x] == '.':
                res.append((x, y))
    return res

def move_south(data, coords):
    for x, y in coords:
        yn = (y + 1) if (y + 1) < h else 0
        data[y][x], data[yn][x] = data[yn][x], data[y][x]

step = 0
while True:
    step += 1
    east = find_east(data)
    move_east(data, east)
    south = find_south(data)
    move_south(data, south)
    if (not east) and (not south):
        break

# What is the first step on which no sea cucumbers move?
print('task 1', step)